       // TODO add your handling code here:
       int x = Integer.parseInt(jTextField1.getText());
        int i;
        if (x % 2 == 0) {
            jTextField2.setText("EVEN");
            jTextField1.setText("");
        } else {
            jTextField2.setText("ODD");
            jTextField1.setText("");
        }
